import React, { Component, useState } from 'react';
import { 
    StyleSheet, 
    SafeAreaView, 
    Text,
    View,
    TextInput,
    KeyboardAvoidingView,
    Platform
} from 'react-native';

/**
 * Customer Add Screen
 * 
 */
function CustomerAdd({ navigation }) {
    const [name_input, onChangeName] = useState('');
    const [phone_input, onChangePhone] = useState('');
    const [party_input, onChangeParty] = useState('');
    
    return (
      /**
       * KeyboardAvoidingView to solve the keyboard pushing layout out of order problem
       */
      <KeyboardAvoidingView style={styles.container} behavior={Platform.OS === "ios" ? "padding" : "height"}>
          <SafeAreaView style={styles.waitlist_container}>
              <View style={styles.info_container}>
                
                <TextInput
                  style={styles.input}
                  clearTextOnFocus={true}
                  placeholder={'Enter name here'}
                  onChangeText={text => onChangeName(text)}
                  value={name_input}
                >
  
                </TextInput>
  
                <TextInput
                  style={styles.input}
                  clearTextOnFocus={true}
                  placeholder={'Enter phone number here'}
                  onChangeText={text => onChangePhone(text)}
                  value={phone_input}
                >
  
                </TextInput>
  
                <TextInput
                  style={styles.input}
                  clearTextOnFocus={true}
                  placeholder={'Enter party size here'}
                  onChangeText={text => onChangeParty(text)}
                  value={party_input}
                >
  
                </TextInput>
  
  
              </View>
  
              
              <TouchableOpacity
                  style={styles.add_btn}
                  onPress={() =>  {
  
                    if (name_input == "" || phone_input == "" || party_input == "") {
                        console.log("None of the fields can be empty!")
                    } else {
                        console.log("Name: " + name_input);
                        console.log("Phone: " + phone_input);
                        console.log("Party size: " + party_input);
                        navigation.navigate('HOME');
                    }
                    
                  }}
              >
                  <Text>ADD</Text>
              </TouchableOpacity>
          </SafeAreaView>
        </KeyboardAvoidingView>
    );
  }


const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      justifyContent: 'flex-end',
    },

    waitlist_container: {
        flex: 1,
        backgroundColor: '#fff',
        justifyContent: 'flex-end',
        alignItems: 'center'
    },
    
      info_container: {
        position: 'absolute',
        width: '80%',
        height: '50%',
        bottom: '30%',
        backgroundColor: '#fff',
        justifyContent: 'space-between',
        alignItems: 'center'
    },

    add_btn: {
        width: '80%',
        height: 60,
        bottom: 20,
        backgroundColor: 'dodgerblue',
        justifyContent: 'center',
        alignItems: 'center',
        alignSelf: 'center'
    },

    input: {
    
        paddingLeft: 10,
        height: 50,
        width: '80%',
        borderColor: 'black',
        borderWidth: 1
    }
});

export default CustomerAdd;
