import React, { Component, useState } from 'react';
import { 
    StyleSheet, 
    SafeAreaView, 
    Button, 
    View,
    Text,
    TouchableOpacity,
    Modal
} from 'react-native';



function CustomerHome() {
    const [modalVisible, setModalVisible] = useState(false);

    return (
        <SafeAreaView style={styles.container}>
            
            <View style={styles.tble}>

            </View>
            
            <TouchableOpacity
                style={styles.JOIN_WAITLIST}
                onPress={() => setModalVisible(!modalVisible)}
            >
                <Text>JOIN</Text>
            </TouchableOpacity>

            <Modal 
                animationType={'slide'}
                visible={modalVisible} 
                transparent={true}
                onPress={() => setModalVisible(!modalVisible)}
            >
                <View style={styles.customer_modal}>
                    <Text style={{ fontSize: 16, top: 20 }}>
                        Please enter your name and phone number below
                    </Text>
                </View>
            </Modal>

        </SafeAreaView>  
    );
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      justifyContent: 'flex-end',
    },

    JOIN_WAITLIST: {
        width: '80%',
        height: 100,
        bottom: 20,
        backgroundColor: 'dodgerblue',
        justifyContent: 'center',
        alignItems: 'center',
        alignSelf: 'center'
    },

    customer_modal: {
        flex: 1,
        backgroundColor: 'red',
        alignContent: 'center',
        alignItems: 'center',
        top: '15%'
    },

    tble: {
        bottom: 40,
        alignSelf: 'center',
        width: '80%',
        height: '70%',
        backgroundColor: 'gray'
    }

});

export default CustomerHome;
