import 'react-native-gesture-handler';
import React, { useState } from 'react';
import { 
  StyleSheet, 
  SafeAreaView, 
  View,
  Text,
  TouchableOpacity,
  TextInput
} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

//import CustomerHome from './app/assets/CustomerHome';


const Stack = createStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator>
          <Stack.Screen 
              name="HOME" 
              component={CustomerHome}
          />

          <Stack.Screen 
              name="ADD TO WAITLIST"
              component={CustomerAdd}
          />
      </Stack.Navigator>
  </NavigationContainer>
  );
}


/**
 * Customer Home Screen
 * 
 */
function CustomerHome({ navigation }) {

  return (
      <SafeAreaView style={styles.container}>

          <View style={styles.tble}>

          </View>

          <TouchableOpacity
              style={styles.join_btn}
              onPress={() =>  navigation.navigate('ADD TO WAITLIST')}
          >
              <Text>JOIN</Text>
          </TouchableOpacity>

      </SafeAreaView>  
  );
}

/**
 * Customer Add Screen
 * 
 */
function CustomerAdd({ navigation }) {
  const [name_input, onChangeName] = useState('');
  const [phone_input, onChangePhone] = useState('');
  const [party_input, onChangeParty] = useState('');

  return (
      <SafeAreaView style={styles.waitlist_container}>
          <View style={styles.info_container}>

            <TextInput
              style={styles.input}
              clearTextOnFocus={true}
              placeholder={'Enter name here'}
              onChangeText={text => onChangeName(text)}
              value={name_input}
            >

            </TextInput>

            <TextInput
              style={styles.input}
              clearTextOnFocus={true}
              placeholder={'Enter phone number here'}
              onChangeText={text => onChangePhone(text)}
              value={phone_input}
            >

            </TextInput>

            <TextInput
              style={styles.input}
              clearTextOnFocus={true}
              placeholder={'Enter party size here'}
              onChangeText={text => onChangeParty(text)}
              value={party_input}
            >

            </TextInput>


          </View>


          <TouchableOpacity
              style={styles.add_btn}
              onPress={() =>  {

                if (name_input == "" || phone_input == "" || party_input == "") {
                    console.log("None of the fields can be empty!")
                } else {
                    console.log("Name: " + name_input);
                    console.log("Phone: " + phone_input);
                    console.log("Party size: " + party_input);
                    navigation.navigate('HOME');
                }

              }}
          >
              <Text>ADD</Text>
          </TouchableOpacity>
      </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    justifyContent: 'flex-end',
  },

  waitlist_container: {
    flex: 1,
    backgroundColor: '#fff',
    justifyContent: 'flex-end',
    alignItems: 'center'
  },

  info_container: {
    position: 'absolute',
    width: '80%',
    height: '50%',
    bottom: '30%',
    backgroundColor: '#fff',
    justifyContent: 'space-between',
    alignItems: 'center'
  },

  join_btn: {
      width: '80%',
      height: 60,
      bottom: 20,
      backgroundColor: 'dodgerblue',
      justifyContent: 'center',
      alignItems: 'center',
      alignSelf: 'center'
  },

  add_btn: {
    position: 'absolute',
    width: '80%',
    height: 60,
    bottom: 20,
    backgroundColor: 'dodgerblue',
    justifyContent: 'center',
    alignItems: 'center',
    alignSelf: 'center'
  },

  tble: {
      position: 'absolute',
      bottom: '20%',
      alignSelf: 'center',
      width: '80%',
      height: '70%',
      backgroundColor: 'gray'
  },

  input: {

    paddingLeft: 10,
    height: 50,
    width: '80%',
    borderColor: 'black',
    borderWidth: 1
  }
});
